    public static function LoadEntityByAlbum($album_id)
    {
        $q_meta = Doctrine_Query::create()
            ->select()
            ->from("AlbumMeta am")
            ->where("am.album_id = ?", $album_id);
        $o_meta = $q_meta->fetchOne();
